import React from 'react';
import PropTypes from 'prop-types';
import { withStyles, getMuiTheme } from '@material-ui/core/styles';
import { Slider } from '@material-ui/lab';
import { reduce } from 'lodash';

const styles = {
  root: {
    width: '100%',
    display: 'flex',
    '& div': {
      background: 'none'
    }
  },
  slider: {
    padding: '10px 5px'
  },
  thumbWrapper: {
    display: 'none'
  },
  trackBeforeOdd: {
    background: 'red !important'
  },
  trackBeforeEven: {
    background: 'blue !important'
  }
};

class TimeSlider extends React.Component {
  state = {
    scale: 1
  };

  componentWillMount() {
    const { data } = this.props;
    this.sliderScale(data);
  }

  componentWillReceiveProps(props) {
    const { data } = props;
    this.sliderScale(data);
  }

  sliderScale = data => {
    const total =
      reduce(
        data,
        (sum, n) => {
          return sum + n;
        },
        0
      ) || 100;
    this.setState({ scale: 100 / total });
  };

  render() {
    const { classes, data } = this.props;
    const { scale } = this.state;
    const theme = getMuiTheme({
      slider: {
        selectionColor: '#F76B1C',
        handleFillColor: '#F76B1C'
      }
    });

    return (
      <div className={classes.root}>
        {data.map((d, i) => {
          return (
            <Slider
              theme={theme}
              key={i}
              classes={{
                container: classes.slider,
                thumbWrapper: classes.thumbWrapper,
                trackBefore:
                  i % 2 ? classes.trackBeforeEven : classes.trackBeforeOdd
              }}
              value={100}
              style={{ width: `${d * scale}%` }}
              aria-labelledby="label"
              thumb={<div />}
            />
          );
        })}
      </div>
    );
  }
}

TimeSlider.defaultProps = {
  data: [100, 20, 50, 60]
};

TimeSlider.propTypes = {
  classes: PropTypes.object.isRequired,
  data: PropTypes.array
};

export default withStyles(styles)(TimeSlider);
