import { ADD_STOCKS } from '../actionType';

const stocks = (state = [], action) => {
  switch (action.type) {
    case ADD_STOCKS:
      return (
        action.data.map(d => {
          console.log(state);
          return [...d, new Date()];
        }) || state
      );
    default:
      return state;
  }
};

export default stocks;
